package a_datatype;

public class Ex03_나머지 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//나머지 연산자 활용 : 홀/짝수 구할 때
		int a = 10;
		
		if( a % 2 == 0) {
			System.out.println("짝수");
		} else {
			System.out.println("홀수");
		}
		int b = 11;
		
		if( b % 2 == 0) {
			System.out.println("짝수");
		} else {
			System.out.println("홀수");
		}
	}


	
	
	
}

