package a_datatype;

  
 // 산술연산자 : + - */ %
    
 
public class Ex03_산술 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20;
		int b = 15;
		
		System.out.println("20 + 15 = " + (a + b));
		System.out.println("20 - 15 = " + (a - b));
		System.out.println("20 x 15 = " + (a * b));
		System.out.println("20 / 15 = " + (a / b));
		System.out.println("the remain of 20 / 15 = " + (a % b));
	}

}
